        static void moving(string input, float x, float y)
        {
            if (input.ToUpper() == "U")
            {
                y--;
            }
            if (input.ToUpper() == "D")
            {
                y++;
            }
            if (input.ToUpper() == "L")
            {
                x--;
            }
            if (input.ToUpper() == "R")
            {
                x++;
            }

